const App = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <Router>
      <GlobalStyle />
      <StarryBackground />
      <Navigation>
        <NavContainer>
          <NavTitle to="/">Research Voyager</NavTitle>
          <HamburgerIcon onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <FontAwesomeIcon icon={faBars} />
          </HamburgerIcon>
        </NavContainer>
      </Navigation>
      <AppContainer>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/projects" component={Projects} />
          <Route path="/project/:projectTitle" component={ProjectDetailsPage} />
          <Route path="/researcher/:researcherId" component={ResearcherProfilePage} />
          <Route path="/search" component={SearchResults} />
        </Switch>
      </AppContainer>
    </Router>
  );
};

export default App;
