import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import styled from 'styled-components';

const ResultsContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
`;

const ResultItem = styled(Link)`
  display: block;
  margin-bottom: 20px;
  padding: 10px;
  background-color: rgba(255, 255, 255, 0.1);
  border-radius: 5px;
  color: white;
  text-decoration: none;

  &:hover {
    background-color: rgba(255, 255, 255, 0.2);
  }
`;

const SearchResults = () => {
  const [results, setResults] = useState([]);
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const query = searchParams.get('query').toLowerCase();

    // This is where you'd normally fetch results from your API
    // For now, we'll filter the existing projects
    const allProjects = [
      ...exampleProjects,
      ...cancerProjects,
      ...influenzaProjects,
      ...coronavirusProjects,
      ...measlesProjects,
      ...sickleCellProjects
    ];

    const filteredResults = allProjects.filter(project => 
      project.title.toLowerCase().includes(query) ||
      (typeof project.postedBy === 'object' && project.postedBy.name.toLowerCase().includes(query)) ||
      (typeof project.postedBy === 'string' && project.postedBy.toLowerCase().includes(query))
    );

    setResults(filteredResults);
  }, [location.search]);

  return (
    <ResultsContainer>
      <h2>Search Results</h2>
      {results.map((result, index) => (
        <ResultItem key={index} to={`/project/${encodeURIComponent(result.title)}`}>
          <h3>{result.title}</h3>
          <p>Posted by: {typeof result.postedBy === 'object' ? result.postedBy.name : result.postedBy}</p>
        </ResultItem>
      ))}
    </ResultsContainer>
  );
};

export default SearchResults;
