// Components
const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const history = useHistory();

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentProjectIndex((prevIndex) => 
        (prevIndex + 1) % exampleProjects.length
      );
    }, 5000);

    return () => clearInterval(timer);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    const validDiseases = ['cancer', 'influenza', 'coronavirus', 'measles', 'sickle cell anemia'];
    const searchLower = searchQuery.toLowerCase();
    if (validDiseases.some(disease => searchLower.includes(disease))) {
      history.push(`/projects?search=${encodeURIComponent(searchQuery)}`);
    } else {
      console.log('No matching disease found. Please try a different search term.');
    }
  };

  return (
    <Section>
      <SectionTitle>Welcome to Research Voyager</SectionTitle>
      <p>Connecting global research talent with US-based institutions and hospitals.</p>
      <form onSubmit={handleSearch}>
        <SearchBar>
          <SearchInput
            type="text"
            placeholder="Search for research projects..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <SearchButton type="submit">Search</SearchButton>
        </SearchBar>
      </form>
      <ProjectCarousel>
        {exampleProjects.map((project, index) => (
          <ProjectCard
            key={index}
            style={{
              transform: `translateY(${(index - currentProjectIndex) * 100}%)`,
              opacity: index === currentProjectIndex ? 1 : 0,
              zIndex: index === currentProjectIndex ? 1 : 0,
            }}
          >
            <ProjectInfo>
              <ProjectTitle>{project.title}</ProjectTitle>
              <p>{project.description}</p>
            </ProjectInfo>
            <ApplyButton onClick={() => history.push(`/project/${encodeURIComponent(project.title)}`)}>Apply</ApplyButton>
          </ProjectCard>
        ))}
      </ProjectCarousel>
    </Section>
  );
};

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [diseaseTitle, setDiseaseTitle] = useState('');
  const history = useHistory();

  useEffect(() => {
    const fetchProjects = () => {
      const searchParams = new URLSearchParams(history.location.search);
      const searchQuery = searchParams.get('search');
      let title = '';
      let dummyProjects = [];

      if (searchQuery.includes('cancer')) {
        title = 'Cancer';
        dummyProjects = cancerProjects;
      } else if (searchQuery.includes('influenza')) {
        title = 'Influenza';
        dummyProjects = influenzaProjects;
      } else if (searchQuery.includes('coronavirus')) {
        title = 'Coronavirus';
        dummyProjects = coronavirusProjects;
      } else if (searchQuery.includes('measles')) {
        title = 'Measles';
        dummyProjects = measlesProjects;
      } else if (searchQuery.includes('sickle cell anemia')) {
        title = 'Sickle Cell Anemia';
        dummyProjects = sickleCellProjects;
      }
      setProjects(dummyProjects);
      setDiseaseTitle(title);
    };

    fetchProjects();
  }, [history.location.search]);

  return (
    <Section>
      <SectionTitle>{diseaseTitle} Research Projects</SectionTitle>
      <ProjectsList>
        {projects.map((project, index) => (
          <ProjectItem key={index}>
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <Button onClick={() => history.push(`/project/${encodeURIComponent(project.title)}`)}>Apply</Button>
          </ProjectItem>
        ))}
      </ProjectsList>
    </Section>
  );
};

const ProjectDetailsPage = () => {
  const { projectTitle } = useParams();
  const [projectDetails, setProjectDetails] = useState(null);

  useEffect(() => {
    const allProjects = [
      ...exampleProjects,
      ...cancerProjects,
      ...influenzaProjects,
      ...coronavirusProjects,
      ...measlesProjects,
      ...sickleCellProjects
    ];

    const project = allProjects.find(p => p.title === decodeURIComponent(projectTitle));
    setProjectDetails(project);
  }, [projectTitle]);

  if (!projectDetails) {
    return <div>Loading...</div>;
  }

  return (
    <Section>
      <ProjectDetails>
        <h2>{projectDetails.title}</h2>
        <p><strong>Posted By:</strong> {projectDetails.postedBy}</p>
        <h3>Job Title: {projectDetails.jobTitle}</h3>
        <h4>Project Summary:</h4>
        <p>{projectDetails.summary}</p>
        <h4>Role Details:</h4>
        <ul>
          {projectDetails.roleDetails.map((detail, index) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>
        <h4>Compensation Details:</h4>
        <p>{projectDetails.compensation}</p>
        <h4>Project Timeline:</h4>
        <p>Start Date: {projectDetails.projectTimeline.start}</p>
        <p>End Date: {projectDetails.projectTimeline.end}</p>
        <h4>Role Timeline:</h4>
        <p>Start Date: {projectDetails.roleTimeline.start}</p>
        <p>End Date: {projectDetails.roleTimeline.end}</p>
        <h4>Qualifications:</h4>
        <ul>
          {projectDetails.qualifications.map((qualification, index) => (
            <li key={index}>{qualification}</li>
          ))}
        </ul>
        <h4>Application Instructions:</h4>
        <p>{projectDetails.applicationInstructions}</p>
        <h4>Additional Information:</h4>
        <p>{projectDetails.additionalInfo}</p>
      </ProjectDetails>
    </Section>
  );
};

const App = () => {
  return (
    <Router>
      <GlobalStyle />
      <AppContainer>
        <Header>
          <HeaderTitle>Research Voyager</HeaderTitle>
        </Header>
        <Navigation>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/researcher">Researcher Profile</NavLink>
          <NavLink to="/institution">Institution Dashboard</NavLink>
        </Navigation>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/researcher" component={() => <div>Researcher Profile (placeholder)</div>} />
          <Route path="/institution" component={() => <div>Institution Dashboard (placeholder)</div>} />
          <Route path="/projects" component={Projects} />
          <Route path="/project/:projectTitle" component={ProjectDetailsPage} />
        </Switch>
      </AppContainer>
    </Router>
  );
};

export default App;
