const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const history = useHistory();

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentProjectIndex((prevIndex) => 
        (prevIndex + 1) % exampleProjects.length
      );
    }, 5000);

    return () => clearInterval(timer);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    const validDiseases = ['cancer', 'influenza', 'coronavirus', 'measles', 'sickle cell anemia'];
    const searchLower = searchQuery.toLowerCase();
    if (validDiseases.some(disease => searchLower.includes(disease))) {
      history.push(`/projects?search=${encodeURIComponent(searchQuery)}`);
    } else {
      console.log('No matching disease found. Please try a different search term.');
    }
  };

  return (
    <Section>
      <SectionTitle>Welcome to Research Voyager</SectionTitle>
      <p>Connecting global research talent with US-based institutions and hospitals.</p>
      <form onSubmit={handleSearch}>
        <SearchBar>
          <SearchInput
            type="text"
            placeholder="Search for research projects..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <SearchButton type="submit">Search</SearchButton>
        </SearchBar>
      </form>
      <ProjectCarousel>
        {exampleProjects.map((project, index) => (
          <ProjectCard
            key={index}
            style={{
              transform: `translateY(${(index - currentProjectIndex) * 100}%)`,
              opacity: index === currentProjectIndex ? 1 : 0,
              zIndex: index === currentProjectIndex ? 1 : 0,
            }}
          >
            <ProjectInfo>
              <ProjectTitle>{project.title}</ProjectTitle>
              <p>{project.description}</p>
            </ProjectInfo>
            <ApplyButton onClick={() => history.push(`/project/${encodeURIComponent(project.title)}`)}>Apply</ApplyButton>
          </ProjectCard>
        ))}
      </ProjectCarousel>
    </Section>
  );
};

