const InstitutionDashboard = () => {
  const [researchPositions, setResearchPositions] = useState([]);
  const [newPosition, setNewPosition] = useState({
    title: '',
    description: '',
    requirements: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPosition({ ...newPosition, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setResearchPositions([...researchPositions, newPosition]);
    setNewPosition({ title: '', description: '', requirements: '' });
  };

  return (
    <Section>
      <SectionTitle>Institution Dashboard</SectionTitle>
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          name="title"
          value={newPosition.title}
          onChange={handleInputChange}
          placeholder="Position Title"
          required
        />
        <TextArea
          name="description"
          value={newPosition.description}
          onChange={handleInputChange}
          placeholder="Position Description"
          required
        />
        <Input
          type="text"
          name="requirements"
          value={newPosition.requirements}
          onChange={handleInputChange}
          placeholder="Requirements"
          required
        />
        <Button type="submit">Post New Position</Button>
      </Form>
      <SectionTitle>Current Open Positions</SectionTitle>
      {researchPositions.map((position, index) => (
        <PositionCard key={index}>
          <PositionTitle>{position.title}</PositionTitle>
          <p>{position.description}</p>
          <p><strong>Requirements:</strong> {position.requirements}</p>
        </PositionCard>
      ))}
    </Section>
  );
};

