  useEffect(() => {
    const allProjects = [
      ...exampleProjects,
      ...cancerProjects,
      ...influenzaProjects,
      ...coronavirusProjects,
      ...measlesProjects,
      ...sickleCellProjects
    ];

    const project = allProjects.find(p => p.title === decodeURIComponent(projectTitle));
    setProjectDetails(project);
  }, [projectTitle]);

  if (!projectDetails) {
    return <div>Loading...</div>;
  }

  return (
    <Section>
      <ProjectDetails>
